mapper.insert:
INSERT INTO account(id, nickname, username, password, email, gender, age)
     VALUES(account_seq.NEXTVAL, #{nickname}, #{username}, #{password}
           , #{email}, #{gender}, #{age})

mapper.update:
UPDATE account
   SET nickname = #{nickname}
     , gender = #{gender}
     , age = #{age}
 WHERE id = #{id}

UPDATE account
   SET expiredate = SYSDATE
 WHERE id = #{id}
