mapper.update:
UPDATE board
   SET title = #{title}
     , btype = #{btype}
     , contents = #{contents}
     , nodel = #{nodel}
     , udate = SYSDATE
 WHERE id = #{id}

UPDATE board
   SET vcnt = vcnt + 1
 WHERE id = #{id}

UPDATE board
   SET gcnt = gcnt + 1
 WHERE id = #{id}

UPDATE board
   SET bcnt = bcnt + 1
 WHERE id = #{id}
